const hamburger=document.querySelector(".burger-menu"),
navMenu=document.querySelector(".header-nav");

hamburger.addEventListener("click",(()=>{
    hamburger.classList.toggle("active"),navMenu.classList.toggle("header-nav--active")})),
    document.querySelectorAll(".header-nav__link").forEach((e=>e.addEventListener("click",(()=>{
        hamburger.classList.remove("active"),navMenu.classList.remove("header-nav--active")}))));